package com.invengo.lib.system.device;import android.os.Build;import com.invengo.lib.diagnostics.InvengoLog;import com.invengo.lib.system.device.type.BarcodeModuleType;import com.invengo.lib.system.device.type.DeviceType;import com.invengo.lib.system.device.type.RfidModuleType;/* * 1.8.2015090100 : ??ĳ?? ?? ??? * 1.9.2015100100 : atid.system.ctrl 1.11.2015100100 ??????? ??? * 1.10.2015111100 : ?α? ??? ??? ???? [Log => ATLog] * 1.11.2015111200 : RFID ?????? ??? ??? ???? ???? * 1.12.2015112000 : OS?? XC1003?? ?ν?????? ??? * 1.11.2016012500 : AT870A 일때 AT911N으로 리턴하던 것을 AT870A로 리턴하도록 수정. * 1.12.2016041800 : AT870A RFID 적용. */// ------------------------------------------------------------------------// Module  Control ( Use Lib Only )// ------------------------------------------------------------------------public class DeviceManager {	private static final String TAG = DeviceManager.class.getSimpleName();	private static final String VERSION = "1.0";	private static final String MODEL_AT911 = "AT911";	private static final String MODEL_AT911_SPACE = "AT 911";	private static final String MODEL_AT911N = "AT911N";	private static final String MODEL_AT511 = "AT511";	private static final String MODEL_XCRF1003 = "XCRF1003";	private static final String MODEL_XC1003 = "XC1003";	private static final String MODEL_AT311 = "AT311";	private static final String MODEL_AT312 = "AT312";	private static final String MODEL_AT911_HILTI_US = "2085958.01";	private static final String MODEL_AT911_HILTI_EU = "2088529.01";	private static final String MODEL_AT911N_HILTI_US = "2085958.02";	private static final String MODEL_AT911N_HILTI_EU = "2088529.02";	private static final String MODEL_AT870A = "AT870A";	private static final String MODEL_XC2910 = "XC2910";	//	private static final String DISPLAY_XC2910 = "XC2910(V3.0)";	private static final String MODEL_XC2900 = "XC2900(V3.0)";	//	private static final String DISPLAY_XC2900 = "XC2900(V3.0)";	private static final String MODEL_XC2903 = "XC2903(V3.0)";	//	private static final String DISPLAY_XC2903 = "XC2903(V3.0)";	private static final String MODEL_XC2910_V3 = "XC2910-A";	//	private static final String MODEL_XC2900 = "c620abc_37t_factory";	private static final String MODEL_XC9910 = "XC9910";	private static DeviceType smDevType = DeviceType.Unknown;	// getDeviceType Method reteieve ATID Device Kinds	public static DeviceType getDeviceType() {		if (smDevType != DeviceType.Unknown)			return smDevType;		String model = Build.MODEL;		String display = Build.DISPLAY;		InvengoLog.i(TAG, "INFO. Model [%s] , Dispaly [%s]", model, display);		if ((model.equals(MODEL_AT911) || model.equals(MODEL_AT911_SPACE))) {			smDevType = DeviceType.AT911;		} else if (model.equals(MODEL_AT911N)) {			smDevType = DeviceType.AT911N;		} else if (model.equals(MODEL_AT511)) {			smDevType = DeviceType.AT511;		} else if (model.equals(MODEL_XCRF1003) || model.equals(MODEL_XC1003)) {			smDevType = DeviceType.XCRF1003;		} else if (model.equals(MODEL_AT311)) {			smDevType = DeviceType.AT311;		} else if (model.equals(MODEL_AT312)) {			smDevType = DeviceType.AT312;		} else if (model.startsWith(MODEL_AT911_HILTI_US)) {			smDevType = DeviceType.AT911_HILTI_US;		} else if (model.startsWith(MODEL_AT911_HILTI_EU)) {			smDevType = DeviceType.AT911_HILTI_EU;		} else if (model.startsWith(MODEL_AT911N_HILTI_US)) {			smDevType = DeviceType.AT911N_HILTI_US;		} else if (model.startsWith(MODEL_AT911N_HILTI_EU)) {			smDevType = DeviceType.AT911N_HILTI_EU;		} else if (model.equals(MODEL_AT870A)) {			smDevType = DeviceType.AT870A;		} else if(model.equals(MODEL_XC2910)) {//add by invengo at 2017.04.26			smDevType = DeviceType.XC2910;		} else if(model.equals(MODEL_XC2900)){			smDevType = DeviceType.XC2900;		} else if(model.equals(MODEL_XC2903)){			smDevType = DeviceType.XC2903;		}else if(model.equals(MODEL_XC2910_V3)){			smDevType = DeviceType.XC2910_V3;		}else if(model.equals(MODEL_XC9910)){			smDevType = DeviceType.XC9910;		}else{			smDevType = DeviceType.Unknown;		}		InvengoLog.i(TAG, "INFO. Device Type is [%s]", smDevType.toString());		return smDevType;	}	/*	 * getRfidType Method retreive RFID module type in ATID device	 */	public static RfidModuleType getRfidType() {		DeviceType type = DeviceManager.getDeviceType();		switch (type) {			case AT911:				break;			case AT911N:			case XC2910://add by invengo at 2017.04.26			case XC9910:			case AT870A:				break;			case AT511:				break;			case AT311:			case AT312:			case XCRF1003:			case XC2900:			case XC2903:			case XC2910_V3:				return RfidModuleType.InvengoX00S_1;//modify by invengo at 2017.02.27			default:				break;		}		return RfidModuleType.None;	}	/*	 * getBarcodeType Method retreive Barcode Scanner module type in ATID device	 */	public static BarcodeModuleType getBarcodeType() {		DeviceType type = DeviceManager.getDeviceType();		switch (type) {			case AT911:				break;			case AT911N:			case XC2910://add by invengo at 2017.04.26			case XC9910:			case AT870A:				break;			case AT511:				break;			case AT311:			case AT312:			case XCRF1003:				return BarcodeModuleType.Invengo2D4710M_1;//modify by invengo at 2017.02.27			default:				break;		}		return BarcodeModuleType.None;	}	// get SDK Version	public static String getVersion() {		return VERSION;	}}